import { createAsyncThunk, createSlice, createEntityAdapter, EntityAdapter } from '@reduxjs/toolkit';
import axios from 'axios';

import api from '../../constants/api';
import { Products, Item } from '../../types/products';

export interface CounterState {
	status: 'idle' | 'loading' | 'failed' | 'success';
}

const initialState: CounterState = {
	status: 'idle'
};

export const fetchProducts = createAsyncThunk<
	Products,
	{ product: string }
>('home/fetchProducts', async ({ product }) => {

	const url = `${api.url}items?q=${product}`;
	const response = await axios(url);

	return response.data
})

export const productAdapter = createEntityAdapter<Item>({
    selectId: (product) => product.id,
});

export const homeSlice = createSlice({
	name: 'home',
	initialState,
	reducers: {
	},
	extraReducers: (builder) => {
		builder
			.addCase(fetchProducts.pending, (state) => {
				state.status = 'loading';
			})
			.addCase(fetchProducts.fulfilled, (state, action) => {
				state.status = 'success';
			})
			.addCase(fetchProducts.rejected, (state) => {
				state.status = 'failed';
			});
	},
});

export default homeSlice.reducer;
