import React, { useEffect } from 'react'
import { Paper, Box, CircularProgress } from '@mui/material';
import { useSearchParams } from "react-router-dom";

import { useAppSelector, useAppDispatch } from '../../app/hooks';
import { fetchProducts } from './homeSlice';

const Products = () => {

    const [params] = useSearchParams();
    const dispatch = useAppDispatch();
    const products = Object.values(useAppSelector(state => state.home.entities));
    const status = useAppSelector(state => state.home.status);
    console.log("CUANTAS VECES RENDERIZO?");

    useEffect(() => {
        console.log("FUUUCK", status);
        dispatch(fetchProducts({ product: params.get('search') || '' }));
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])


    return (
        <Box display={'flex'} >
            {status === 'loading' && <CircularProgress size={40} color={'secondary'} />}
            {status === 'success' &&
                <Box>
                    <Paper sx={{ width: '100%' }}>
                        {products.map(product => (
                            <Box display={'flex'}>
                                <img style={{ height: 180, width: 180, borderRadius: 4, margin: 16 }} src={product?.picture} alt={product?.title} />
                                <Box>

                                </Box>
                            </Box>
                        ))}
                    </Paper>
                </Box>
            }

        </Box>
    )
}

export default Products;
