import React, { useEffect } from 'react'
import { Paper, Box, CircularProgress, Typography } from '@mui/material';
import { useSearchParams } from "react-router-dom";

import { useAppSelector, useAppDispatch } from '../../app/hooks';
import { fetchProducts } from './homeSlice';

const Products = () => {

    const [params] = useSearchParams();
    const dispatch = useAppDispatch();
    const products = Object.values(useAppSelector(state => state.home.entities));
    const status = useAppSelector(state => state.home.status);

    useEffect(() => {
        console.log("FUUUCK", status);
        dispatch(fetchProducts({ product: params.get('search') || '' }));
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    let pesoARS = Intl.NumberFormat("es-AR", {
        style: "currency",
        currency: "ARS",
        minimumFractionDigits: 0
    });

    return (
        <Box display={'flex'} >
            {status === 'loading' && <CircularProgress size={40} color={'secondary'} />}
            {status === 'success' &&
                <Box width={'100%'}>
                    <Typography fontSize={'18'} sx={{ marginTop: '16px', marginBottom: '16px' }}>Aca va algo</Typography>
                    <Paper >
                        {products.map(product => (
                            <Box display={'flex'}>
                                <img style={{ height: 180, width: 180, borderRadius: 4, margin: 16 }} src={product?.picture} alt={product?.title} />
                                <Box display={'flex'} alignItems={'center'}>
                                    <Box display={'flex'}>
                                        <Typography fontSize={24}>{pesoARS.format(product?.price.amount || 0)}</Typography>
                                        <Typography fontSize={16} sx={{ marginLeft:1}}>{product?.price.decimals}</Typography>
                                    </Box>
                                </Box>
                            </Box>
                        ))}
                    </Paper>
                </Box>
            }

        </Box>
    )
}

export default Products;
